import React, { useContext, useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { Button } from '@mui/material';
import { MovieContext } from '../MovieContext';
import { api } from '../api';
import MovieCard from './MovieCard';

const Home = () => {
  const { trendingMovies, setTrendingMovies, searchResults, setSearchResults, setSearch, darkMode, toggleDarkMode } = useContext(MovieContext);
  const [query, setQuery] = useState('');
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const [filters, setFilters] = useState({ genre: '', year: '', rating: '' });
  const history = useHistory();

  useEffect(() => {
    if (trendingMovies.length === 0) {
      api.getTrendingMovies().then(movies => setTrendingMovies(movies));
    }
  }, [trendingMovies.length, setTrendingMovies]);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    setSearch(query);
    const data = await api.searchMovies(query, 1, filters);
    setSearchResults(data.results || []);
    setPage(1);
    setHasMore((data.total_pages || 1) > 1);
  };

  const loadMore = async () => {
    const nextPage = page + 1;
    const data = await api.searchMovies(query, nextPage, filters);
    setSearchResults(prev => [...prev, ...(data.results || [])]);
    setPage(nextPage);
    setHasMore(nextPage < (data.total_pages || 1));
  };

  const applyFilters = (e) => {
    e.preventDefault();
    if (query.trim()) {
      handleSearch(e);
    }
  };

  return (
    <div className="p-4">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl">Movie Explorer</h2>
        <Button variant="outlined" onClick={toggleDarkMode}>
          {darkMode ? 'Light Mode' : 'Dark Mode'}
        </Button>
      </div>
      <form onSubmit={handleSearch} className="mb-4 flex flex-col sm:flex-row gap-2">
        <input
          className="p-2 border rounded dark:bg-gray-700 dark:text-white flex-1"
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search for a movie"
        />
        <select
          className="p-2 border rounded dark:bg-gray-700 dark:text-white"
          value={filters.genre}
          onChange={(e) => setFilters({ ...filters, genre: e.target.value })}
        >
          <option value="">All Genres</option>
          <option value="action">Action</option>
          <option value="comedy">Comedy</option>
          <option value="drama">Drama</option>
        </select>
        <input
          className="p-2 border rounded dark:bg-gray-700 dark:text-white w-20"
          type="number"
          value={filters.year}
          onChange={(e) => setFilters({ ...filters, year: e.target.value })}
          placeholder="Year"
        />
        <input
          className="p-2 border rounded dark:bg-gray-700 dark:text-white w-20"
          type="number"
          value={filters.rating}
          onChange={(e) => setFilters({ ...filters, rating: e.target.value })}
          placeholder="Rating"
          min="0"
          max="10"
        />
        <Button variant="contained" type="submit">Search</Button>
        <Button variant="outlined" onClick={applyFilters}>Apply Filters</Button>
      </form>
      {searchResults.length > 0 ? (
        <div>
          <div className="flex flex-wrap justify-center">
            {searchResults.map(movie => (
              <MovieCard
                key={movie.id}
                movie={movie}
                trailerUrl={movie.videos && movie.videos.results.length > 0 ? `https://www.youtube.com/watch?v=${movie.videos.results[0].key}` : null}
              />
            ))}
          </div>
          {hasMore && (
            <div className="text-center mt-4">
              <Button variant="contained" onClick={loadMore}>Load More</Button>
            </div>
          )}
        </div>
      ) : (
        <div>
          <h3 className="text-xl mb-2">Trending Movies</h3>
          <div className="flex flex-wrap justify-center">
            {trendingMovies.map(movie => (
              <MovieCard
                key={movie.id}
                movie={movie}
                trailerUrl={movie.videos && movie.videos.results.length > 0 ? `https://www.youtube.com/watch?v=${movie.videos.results[0].key}` : null}
              />
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
