import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { api } from '../api';

const MovieDetails = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    const fetchMovieDetails = async () => {
      const data = await api.getMovieDetails(id);
      setMovie(data);
    };
    fetchMovieDetails();
  }, [id]);

  if (!movie) {
    return <div className="text-center p-4">Loading...</div>;
  }

  if (movie === null) {
    return <div className="text-center p-4">Movie not found</div>;
  }

  const posterUrl = movie.poster_path ? `https://image.tmdb.org/t/p/w500${movie.poster_path}` : 'https://via.placeholder.com/500x750';

  return (
    <div className="p-4 max-w-3xl mx-auto">
      <h2 className="text-2xl mb-4">{movie.title}</h2>
      <img src={posterUrl} alt={movie.title} className="w-full max-w-md mx-auto rounded mb-4" />
      <p className="my-2">{movie.overview}</p>
      <p><strong>Genres:</strong> {movie.genres.map(g => g.name).join(', ')}</p>
      <p><strong>Release Date:</strong> {movie.release_date}</p>
      <p><strong>Rating:</strong> {movie.vote_average}</p>
    </div>
  );
};

export default MovieDetails;
