import React, { useContext, useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { Button } from '@mui/material';
import { MovieContext } from '../MovieContext';
import { api } from '../api';
import MovieCard from './MovieCard';
import InfiniteScroll from 'react-infinite-scroll-component';

const Home = () => {
  const { trendingMovies, setTrendingMovies, searchResults, setSearchResults, setSearch, darkMode, toggleDarkMode } = useContext(MovieContext);
  const [query, setQuery] = useState('');
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const history = useHistory();

  useEffect(() => {
    if (trendingMovies.length === 0) {
      api.getTrendingMovies().then(movies => setTrendingMovies(movies));
    }
  }, [trendingMovies.length, setTrendingMovies]);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    setSearch(query);
    const data = await api.searchMovies(query, 1);
    setSearchResults(data);
    setPage(1);
    setHasMore(data.length > 0); // Adjust based on API response structure
  };

  const loadMore = async () => {
    const nextPage = page + 1;
    const data = await api.searchMovies(query, nextPage);
    setSearchResults(prev => [...prev, ...data]);
    setPage(nextPage);
    setHasMore(data.length > 0);
  };

  return (
    <div className="p-4">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl">Movie Explorer</h2>
        <Button variant="outlined" onClick={toggleDarkMode}>
          {darkMode ? 'Light Mode' : 'Dark Mode'}
        </Button>
      </div>
      <form onSubmit={handleSearch} className="mb-4 flex">
        <input
          className="p-2 w-full border rounded dark:bg-gray-700 dark:text-white"
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search for a movie"
        />
        <Button variant="contained" className="ml-2" type="submit">Search</Button>
      </form>
      {searchResults.length > 0 ? (
        <InfiniteScroll
          dataLength={searchResults.length}
          next={loadMore}
          hasMore={hasMore}
          loader={<h4 className="text-center">Loading...</h4>}
          className="flex flex-wrap justify-center"
        >
          {searchResults.map(movie => <MovieCard key={movie.id} movie={movie} />)}
        </InfiniteScroll>
      ) : (
        <div>
          <h3 className="text-xl mb-2">Trending Movies</h3>
          <div className="flex flex-wrap justify-center">
            {trendingMovies.map(movie => <MovieCard key={movie.id} movie={movie} />)}
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
