import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { MovieContext } from '../MovieContext';

const MovieCard = ({ movie }) => {
  const { addToFavorites, removeFromFavorites, favorites, darkMode, getGenreName } = useContext(MovieContext);

  const isFavorite = favorites.some(fav => fav.id === movie.id);

  const handleFavoriteToggle = (e) => {
    e.preventDefault();
    e.stopPropagation();

    if (isFavorite) {
      removeFromFavorites(movie.id);
    } else {
      addToFavorites(movie);
    }
  };

  const getPosterUrl = (path) => {
    if (!path) return '/placeholder-poster.jpg';
    return `https://image.tmdb.org/t/p/w500${path}`;
  };

  // Format release year from full date (e.g., "2023-12-15" -> "2023")
  const getReleaseYear = (dateString) => {
    if (!dateString) return 'Unknown';
    return dateString.split('-')[0];
  };

  // Get primary genre name if genres are provided
  const getPrimaryGenre = () => {
    if (movie.genre_ids && movie.genre_ids.length > 0) {
      return getGenreName(movie.genre_ids[0]);
    }
    return null;
  };

  const primaryGenre = getPrimaryGenre();
  const releaseYear = getReleaseYear(movie.release_date);
  const rating = movie.vote_average ? Math.round(movie.vote_average * 10) / 10 : null;

  return (
    <Link to={`/movie/${movie.id}`} className="group">
      <div className={`relative rounded-lg overflow-hidden shadow-lg transform transition duration-300 hover:scale-105 ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
        {/* Poster Image */}
        <div className="relative aspect-[2/3]">
          <img 
            src={getPosterUrl(movie.poster_path)} 
            alt={movie.title} 
            className="w-full h-full object-cover"
            onError={(e) => {
              e.target.onerror = null;
              e.target.src = '/placeholder-poster.jpg';
            }}
          />

          {/* Favorite Button */}
          <button
            onClick={handleFavoriteToggle}
            className="absolute top-2 right-2 bg-black bg-opacity-60 rounded-full p-2 transition-transform hover:scale-110"
          >
            <svg 
              className={`w-5 h-5 ${isFavorite ? 'text-yellow-500' : 'text-white'}`} 
              fill={isFavorite ? 'currentColor' : 'none'} 
              stroke="currentColor" 
              viewBox="0 0 24 24"
            >
              <path 
                strokeLinecap="round" 
                strokeLinejoin="round" 
                strokeWidth="2" 
                d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" 
              />
            </svg>
          </button>

          {/* Rating Badge */}
          {rating && (
            <div className="absolute bottom-2 left-2 bg-black bg-opacity-70 rounded-full px-2 py-1 flex items-center">
              <svg className="w-4 h-4 text-yellow-500 mr-1" fill="currentColor" viewBox="0 0 20 20">
                <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
              </svg>
              <span className="text-white text-xs font-medium">{rating}</span>
            </div>
          )}
        </div>

        {/* Movie Info */}
        <div className="p-4">
          <h3 className={`font-semibold line-clamp-2 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
            {movie.title}
          </h3>

          <div className="flex items-center mt-2 text-sm">
            {releaseYear && (
              <span className={`${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                {releaseYear}
              </span>
            )}

            {primaryGenre && releaseYear && (
              <span className={`mx-2 ${darkMode ? 'text-gray-500' : 'text-gray-400'}`}>â€¢</span>
            )}

            {primaryGenre && (
              <span className={`${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                {primaryGenre}
              </span>
            )}
          </div>

          {/* Truncated Overview */}
          {movie.overview && (
            <p className={`mt-2 text-sm line-clamp-2 ${darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
              {movie.overview}
            </p>
          )}
        </div>
      </div>
    </Link>
  );
};

export default MovieCard;
