import React, { useContext, useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { Button } from '@mui/material';
import { MovieContext } from '../MovieContext';
import { api } from '../api';
import MovieCard from './MovieCard';
import Favorites from './Favourites';

const Home = () => {
  const { trendingMovies, setTrendingMovies, searchResults, setSearchResults, darkMode, toggleDarkMode, clearFavorites, setToken } = useContext(MovieContext);
  const [query, setQuery] = useState('');
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const history = useHistory();

  useEffect(() => {
    if (trendingMovies.length === 0) {
      api.getTrendingMovies().then(movies => setTrendingMovies(movies));
    }
  }, [trendingMovies.length, setTrendingMovies]);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    const data = await api.searchMovies(query, 1);
    setSearchResults(data.results || []);
    setPage(1);
    setHasMore((data.total_pages || 1) > 1);
  };

  const loadMore = async () => {
    const nextPage = page + 1;
    const data = await api.searchMovies(query, nextPage);
    setSearchResults(prev => [...prev, ...(data.results || [])]);
    setPage(nextPage);
    setHasMore(nextPage < (data.total_pages || 1));
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    clearFavorites(); // Clear favorites state
    setToken(null); // Update token to null to reset favorites fetch
    history.push('/');
  };

  return (
    <div className="p-4">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl">Movie Explorer</h2>
        <div className="flex gap-2">
          <Button variant="outlined" onClick={toggleDarkMode}>
            {darkMode ? 'Light Mode' : 'Dark Mode'}
          </Button>
          <Button variant="outlined" color="secondary" onClick={handleLogout}>
            Logout
          </Button>
        </div>
      </div>
      <form onSubmit={handleSearch} className="mb-4 flex flex-col sm:flex-row gap-2">
        <input
          className="p-2 border rounded dark:bg-gray-700 dark:text-white flex-1"
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search for a movie"
        />
        <Button variant="contained" type="submit">Search</Button>
      </form>
      {searchResults.length > 0 ? (
        <div>
          <div className="flex flex-wrap justify-center">
            {searchResults.map(movie => (
              <MovieCard key={movie.id} movie={movie} />
            ))}
          </div>
          {hasMore && (
            <div className="text-center mt-4">
              <Button variant="contained" onClick={loadMore}>Load More</Button>
            </div>
          )}
        </div>
      ) : (
      <div>
          <div className="mt-8">
            <Favorites />
          </div>
          <h3 className="text-xl mb-2">Trending Movies</h3>
          <div className="flex flex-wrap justify-center">
            {trendingMovies.map(movie => (
              <MovieCard key={movie.id} movie={movie} />
            ))}
          </div>
        </div>
      )}

    </div>
  );
};

export default Home;
