import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { Button } from '@mui/material';
import axios from 'axios';

const Login = () => {
  const history = useHistory();
  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const url = isSignup ? 'http://localhost:5000/signup' : 'http://localhost:5000/login';
      const response = await axios.post(url, { username, password });
      if (isSignup) {
        setIsSignup(false);
        setError('Signup successful! Please log in.');
      } else {
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('userId', response.data.userId);
        history.push('/home');
      }
    } catch (err) {
      setError(err.response?.data?.error || 'An error occurred');
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900">
      <div className="p-6 bg-white dark:bg-gray-800 rounded shadow-md w-full max-w-md">
        <h2 className="text-2xl mb-4 text-center">{isSignup ? 'Sign Up' : 'Login'}</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          {error && <p className="text-red-500 text-sm">{error}</p>}
          <Button variant="contained" type="submit" fullWidth>
            {isSignup ? 'Sign Up' : 'Login'}
          </Button>
          <Button
            variant="outlined"
            fullWidth
            onClick={() => setIsSignup(!isSignup)}
          >
            {isSignup ? 'Switch to Login' : 'Switch to Sign Up'}
          </Button>
        </form>
      </div>
    </div>
  );
};

export default Login;
