import axios from 'axios';

const API_KEY = process.env.REACT_APP_TMDB_API_KEY;
const BASE_URL = 'https://api.themoviedb.org/3';

export const api = {
  getTrendingMovies: async () => {
    try {
      const response = await axios.get(`${BASE_URL}/trending/movie/day?api_key=${API_KEY}&append_to_response=videos`);
      return response.data.results || [];
    } catch (error) {
      console.error('Error fetching trending movies:', error);
      return [];
    }
  },
  searchMovies: async (query, page = 1, filters = { genre: '', year: '', rating: '' }) => {
    try {
      let url = `${BASE_URL}/search/movie?api_key=${API_KEY}&query=${encodeURIComponent(query)}&page=${page}&append_to_response=videos`;
      if (filters.genre) url += `&with_genres=${filters.genre}`;
      if (filters.year) url += `&year=${filters.year}`;
      if (filters.rating) url += `&vote_average.gte=${filters.rating}`;
      const response = await axios.get(url);
      return {
        results: response.data.results || [],
        total_pages: response.data.total_pages || 1
      };
    } catch (error) {
      console.error('Error searching movies:', error);
      return { results: [], total_pages: 1 };
    }
  },
  getMovieDetails: async (movieId) => {
    try {
      const response = await axios.get(`${BASE_URL}/movie/${movieId}?api_key=${API_KEY}&append_to_response=videos`);
      return response.data || null;
    } catch (error) {
      console.error('Error fetching movie details:', error);
      return null;
    }
  }
};
